CREATE TABLE IF NOT EXISTS member_infos(
  id INT  NOT NULL PRIMARY KEY AUTO_INCREMENT,
  name VARCHAR(255) NOT NULL,
  nickname VARCHAR(255) NOT NULL,
  image_link_url VARCHAR(255) NOT NULL,
  birthday_year INT NOT NULL,
  birthday_month INT NOT NULL,
  birthday_day INT NOT NULL,
  blood_type VARCHAR(255) NOT NULL,
  place_of_birth VARCHAR(255) NOT NULL,
  height INT NOT NULL,
  hobby VARCHAR(255) NOT NULL,
  special_skill VARCHAR(255) NOT NULL,
  best_feature VARCHAR(255) NOT NULL,
  member_id INT NOT NULL,
  create_at Datetime NOT NULL DEFAULT CURRENT_TIMESTAMP,
  update_at Datetime NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,

  FOREIGN KEY(member_id) REFERENCES members(id)
);


